import React from "react";
import "./styles.css";

export default function App() {
  function handleClick() {
    alert("You clicked me!");
  }

  return (
    <div>
      <Button color="black" text="Hello" disabled={true} />
      <br />
      <Button color="orange" text="Hello 2" disabled={false} />
      <br />
      <Button color="blue" text="Hello 3" disabled={true} />
    </div>
  );
}

function Button({ color, disabled, text }) {
  return (
    <button
      style={{ backgroundColor: color }}
      disabled={disabled}
      onClick={handleClick}
    >
      {text}
    </button>
  );
}
